"use strict";

var mongoose = require('mongoose');
var mongoosePaginate = require('mongoose-paginate-v2');
var reservaSchema = new mongoose.Schema({
  horaInicio: {
    type: Date,
    required: true
  },
  horaFin: {
    type: Date,
    required: true
  },
  precio: {
    type: Number,
    required: true
  },
  reservado: {
    type: Boolean,
    "default": true
  },
  canchaId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Cancha'
  }
});
reservaSchema.plugin(mongoosePaginate);
reservaSchema.pre('save', function (next) {
  if (!this.horaFin) {
    var horaInicio = new Date(this.horaInicio);
    this.horaFin = new Date(horaInicio.getTime() + 3600000); // Agregar una hora en milisegundos
  }
  next();
});
var Reserva = mongoose.model('Reserva', reservaSchema);
module.exports = Reserva;
