// En tu archivo de rutas
const express = require('express');
const router = express.Router();
const React = require('react');
const ReactDOMServer = require('react-dom/server');
const ResetPasswordForm = require('../../src/forms/ResetPasswordForm.jsx'); // Importa tu componente React

// Ruta para restablecer la contraseña
router.get('/reset-password/:token', (req, res) => {
  const token = req.params.token;
  // Renderiza el componente React y envíalo al cliente
  const component = ReactDOMServer.renderToString(React.createElement(ResetPasswordForm, { token }));
  res.send(component);
});

module.exports = router;

